        public static void Add(Window window)
        {
            window.Index = Windows.Count;
            Windows.Add(window);
            window.Active();
        }

        public static void MoveToEnd(Window window)
        {
            if (window.Index == Windows.Count - 1) return;

            int index = window.Index;
            foreach (var v in Windows)
            {
                if (v.Index > index)
                {
                    v.Index--;
                }
            }
            window.Index = Windows.Count - 1;
        }

