        public void DrawCursor(Graphics graphics, int x, int y)
        {
            for (int h = 0; h < 21; h++)
                for (int w = 0; w < 12; w++)
                {
                    if (cursor[h * 12 + w] == 1)
                        graphics.DrawPoint((uint)Color.White.ToArgb(), w + x, h + y);

                    if (cursor[h * 12 + w] == 2)
                        graphics.DrawPoint((uint)Color.Black.ToArgb(), w + x, h + y);
                }
            

